import React from 'react';
import { Link } from 'react-router-dom';
import {motion} from "framer-motion";

const buttonVariants ={
  hover: {
    scale: 1.1, 
    color: "rgb(255, 0, 255)", 
    textShadow: "0px 0px 8px rgb(255, 255, 255)",
    boxShadow: "0px 0px 8px rgb(255, 255, 255)",
    transition: {
      duration: 0.3,
      yoyo: Infinity,
    }
  },
  visible: {
    x: 0
  },
  hidden:{
    x: -1000
  }
}
const containerVariants ={
  hidden: {
    opacity: 0,
  },
  visible:{
    opacity: 1,
    transition: { delay: 0.5, duration: 1.55 }
  },
  exit: {
    x: "-100vw",
    transition: {ease: "easeInOut"}
  }
}

const Home = () => {
  return (
    <motion.div 
      className="home container"
    >
      <motion.h2
        variants={containerVariants} 
        initial="hidden"
        animate="visible"
      >
        Welcome to Pizza Joint
      </motion.h2>
      <Link to="/base">
        <motion.button
          variants={buttonVariants}
          whileHover="hover"
        >
          Create Your Pizza
        </motion.button>
      </Link>
    </motion.div>
  )
}

export default Home;
